delimiter ;;
CREATE TRIGGER `duplicate_config_copy1` BEFORE INSERT ON `configs` FOR EACH ROW BEGIN
	declare keyword1 varchar(255);
	declare count int;
	DECLARE msg VARCHAR(255);
	set keyword1 = NEW.name ;
	set count = (select count(*) from configs where name = keyword1);
	if(count>0)then 
        set msg = "Name exists";
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = msg;
    END IF;
END
;;
delimiter ;

