INSERT INTO `configs` VALUES (72, 'introduce_vct_list_info', '[{\"content\":\"Chủ tịch Hội đồng thành viên Công ty TNHH Tổ hợp Y tế Phương Đông - Ông Nguyễn Thanh Việt cho biết: \\\"Bệnh viện Phương Đông được xây dựng theo tiêu chuẩn 5 sao, đặt mục tiêu trở thành nhà cung cấp hàng đầu các dịch vụ chăm sóc y tế đạt đẳng cấp thế giới thu hút khách hàng trong nước và khu vực châu Á\\\".\",\"name\":\"Tiêu Chuẩn 5 Sao\"},{\"content\":\"Bệnh viện Đa khoa Phương Đông định hướng phát triển trở thành đơn vị y tế hàng đầu khu vực và vươn tầm quốc tế, đem đến các dịch vụ đẳng cấp, góp phần nâng cao chất lượng sống của người Việt.\",\"name\":\"Vươn Tầm Quốc Tế\"},{\"content\":\"Cùng với sứ mệnh “Nâng niu từng sự sống”, mỗi con người tại Phương Đông luôn nỗ lực không ngừng học hỏi và hoàn thiện mình để mang nền y học hiện đại thế giới đến với con người Việt Nam.\",\"name\":\"Nâng Niu Từng Sự Sống\"}]', NULL, 'Nội dung', NULL, 1, NULL, NULL, 54, 'lib_true_text', 2, '2020-11-10 17:11:08', '2021-09-16 16:28:59', 0, NULL, NULL, 2);
