INSERT INTO `configs` VALUES (75, 'introduce_ga_content', '<p>Nằm trên địa bàn phường Cổ Nhuế 2, quận Bắc Từ Liêm, Hà Nội, tọa lạc giữa các khu dân cư có mật độ dân số cao như: khu dân cư Cổ Nhuế, khu dân cư Tây Hồ Tây, khu đô thị Ciputra, khu đô thị Bắc Cổ Nhuế Chèm, Bệnh viện Đa khoa Phương Đông được coi là điểm sáng về y tế khu vực phía Tây Hà Nội, là địa chỉ khám chữa bệnh tin cậy cho gần 180.000 cư dân có nhu cầu sử dụng dịch vụ y tế chất lượng cao.</p>', NULL, 'Nội dung', NULL, 1, NULL, NULL, 55, 'EDITOR', 3, '2020-11-10 17:11:08', '2021-09-16 17:02:06', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (76, 'introduce_about_list_info', '[{\"name\":\"Đứng đầu trong lĩnh vực y tế, luôn đặt uy tín lên hàng đầu.\"},{\"name\":\"Đội ngũ với đầy kinh nghiệm, đam mê và nhiệt huyết trong công việc\"},{\"name\":\"Trang thiết bị hiện đại, hàng đầu thế giới.\"}]', NULL, 'Nội dung list', NULL, 1, NULL, NULL, 53, 'lib_one_text', 3, '2020-11-10 17:11:08', '2021-09-16 18:08:30', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (77, 'introduce_ghm_title', 'Mô hình bệnh viện xanh', NULL, 'Tiêu đề', NULL, 1, NULL, NULL, 56, 'TEXT', 1, '2020-11-10 17:11:08', '2021-09-17 01:29:04', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (78, 'introduce_ghm_content', '<p>Được tư vấn và phát triển bởi Vamed - Tập đoàn đã có nhiều hỗ trợ cho hệ thống y tế công cộng của Việt Nam trong hơn 20 năm qua với mục tiêu hiện đại hoá hệ thống bệnh viện, Bệnh viện Đa khoa Phương Đông là một trong những đơn vị y tế tiên phong tại Việt Nam xây dựng theo mô hình bệnh viện xanh kết hợp điều trị và nghỉ dưỡng thuần tự nhiên. Tòa nhà chính được bao quanh bởi khuôn viên hàng nghìn cây xanh, hồ điều hòa, quảng trường rộng lớn với tổng diện tích gần 10ha.</p>\r\n<p>Với tổng mức đầu tư 198 triệu USD, bệnh viện có quy mô hơn 1.000 giường bệnh. Giai đoạn 1, bệnh viện đưa vào hoạt động 250 giường với các chuyên khoa gồm: Khoa Phụ Sản, Khoa Nhi, Khoa Ngoại, Khoa Nội, Khoa Xét Nghiệm, Khoa Khám Bệnh, Khoa Chẩn Đoán Hình Ảnh, Khoa Liên Chuyên Khoa, Khoa Dược, Trung Tâm Tư vấn và Tiêm chủng Vắc-xin...</p>', NULL, 'Nội dung', NULL, 1, NULL, NULL, 56, 'EDITOR', 2, '2020-11-10 17:11:08', '2021-09-17 01:29:04', 0, NULL, NULL, 2);
