INSERT INTO `configs` VALUES (80, 'introduce_doctor_list_title', 'Đội ngũ bác sĩ giàu kinh nghiệm, giàu tâm huyết', NULL, 'Tiêu đề', NULL, 1, NULL, NULL, 57, 'TEXT', 1, '2020-11-10 17:11:08', '2021-09-17 02:36:46', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (81, 'introduce_doctor_list', '[{\"content\":\"Bệnh viện Đa khoa Phương Đông quy tụ đội ngũ bác sĩ có trình độ chuyên môn cao, giàu kinh nghiệm, có nhiều năm công tác và giữ vị trí quan trọng tại các bệnh viện tuyến trung ương cũng như hợp tác với đông đảo đội ngũ các chuyên gia đầu ngành trong và ngoài nước tư vấn chuyên môn và thăm khám trực tiếp.\"},{\"content\":\"Bên cạnh đó, đội ngũ điều dưỡng, hộ sinh, kỹ thuật viên tại bệnh viện đều là những gương mặt trẻ, nhiệt huyết, giàu kinh nghiệm, được đào tạo bài bản, luôn đề cao tinh thần trách nhiệm, hết lòng tận tụy với công việc, tận tâm với người bệnh, giúp người bệnh an tâm, thoải mái trong suốt quá trình thăm khám và điều trị.\"},{\"content\":\"Thông qua các chương trình hợp tác trong nước và quốc tế, các bác sĩ, điều dưỡng tại Bệnh viện Đa khoa Phương Đông liên tục được cập nhật kiến thức y tế mới nhất, bám sát với sự phát triển của nền y học thế giới.\"}]', NULL, 'Nội dung', NULL, 1, NULL, NULL, 57, 'lib_two_textarea', 2, '2020-11-10 17:11:08', '2021-09-17 02:36:46', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (82, 'introduce_ttb_title', 'Trang thiết bị hiện đại', NULL, 'Tiêu đề', NULL, 1, NULL, NULL, 58, 'TEXT', 1, '2020-11-10 17:11:08', '2021-09-17 03:02:50', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (83, 'introduce_ttb_content', '<p>Bệnh viện Đa khoa Phương Đông đầu tư đồng bộ về cơ sở vật chất, trang thiết bị nhập khẩu từ các thương hiệu hàng đầu thế giới như: Philips (Hà Lan/Mỹ), GE Healthcare (Mỹ), Beckman Coulter (Mỹ), Roche-Hitachi (Nhật Bản), Olympus (Nhật Bản), Nihon Kohden (Nhật Bản),... đảm bảo các tiêu chí về hiệu quả, an toàn, tiết kiệm thời gian, chính xác và thân thiện với môi trường.</p>', NULL, 'Nội dung', NULL, 1, NULL, NULL, 58, 'EDITOR', 2, '2020-11-10 17:11:08', '2021-09-17 03:02:50', 0, NULL, NULL, 2);
