INSERT INTO `configs` VALUES (86, 'introduce_service_content', '[{\"content\":\"Luôn coi người bệnh là trung tâm, mọi sự chăm sóc đều hướng đến xóa bỏ tâm lý sợ hãi, lo lắng, ngột ngạt khi đến bệnh viện, để không chỉ thân bệnh mà cả tâm bệnh cũng được chữa lành.\\n\",\"name\":\"Đội ngũ bác sĩ giàu y đức\"},{\"content\":\"Khách hàng đến với Phương Đông sẽ được trải nghiệm cảm giác thư thái như đang đi nghỉ dưỡng tại một không gian sang trọng, yên bình và xanh trong.\",\"name\":\"Cảm giác thư thái, bình yên\"},{\"content\":\"Bằng việc mở rộng liên kết, hợp tác với các đơn vị Bảo hiểm trên cả nước, Phương Đông đem đến cơ hội khám chữa bệnh chất lượng cao với chuyên gia hàng đầu cùng mức chi phí hợp lý, tiết kiệm cho đông đảo người dân.\",\"name\":\"Chi phí hợp lý, tiết kiệm\"}]', NULL, 'Nội dung', NULL, 1, NULL, NULL, 59, 'lib_true_text', 3, '2020-11-10 17:11:08', '2021-09-17 04:45:13', 0, NULL, NULL, 2);
