INSERT INTO `configs` VALUES (88, 'introduce_cooperate_img', '{\"id\":156,\"name\":\"mask-group_2.png\",\"title\":null,\"caption\":null,\"alt\":null,\"description\":null,\"created_at\":\"2021-09-17T02:05:03.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"mask-group_2.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"156.88 KB\\\",\\\"date\\\":1631844303,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/mask-group_2.png\\\",\\\"width\\\":570,\\\"height\\\":286,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/mask-group_2.png\\\"}\",\"updated_at\":\"2021-09-17T02:05:03.000000Z\",\"trash\":0}', NULL, 'Hình ảnh', NULL, 1, NULL, NULL, 60, 'IMAGEV2', 2, '2020-11-10 17:11:08', '2021-09-17 09:07:31', 0, NULL, NULL, 2);
INSERT INTO `configs` VALUES (89, 'introduce_cooperate_content', '<p>Trong tiến trình phát triển của mình, <strong>Bệnh viện Đa khoa Phương Đông</strong> luôn nỗ lực trau dồi chuyên môn, cập nhật các tiến bộ khoa học kỹ thuật, đồng thời không ngừng hợp tác với các đơn vị y tế đầu ngành trong nước và quốc tế trong thăm khám, điều trị và đào tạo chuyên môn như: Đại học Y Hà Nội, Bệnh viện Nhi Trung ương, Bệnh viện Ung bướu Hà Nội, Bệnh viện Lão khoa Trung ương, Bệnh viện Châm cứu Trung ương, Công ty Philips Việt Nam, Viện Công nghệ sinh học... nhằm hoàn thiện chất lượng dịch vụ, đáp ứng toàn diện nhu cầu khám, chữa bệnh của người dân.</p>', NULL, 'Nội dung', NULL, 1, NULL, NULL, 60, 'EDITOR', 4, '2020-11-10 17:11:08', '2021-09-17 04:52:22', 0, NULL, NULL, 2);
