INSERT INTO `configs` VALUES (92, 'introduce_message_content', '<p style=\"text-align: center;\">“Mục tiêu cuối cùng của cuộc đời là hạnh phúc khi phục vụ người khác!”. Sứ mệnh của Intracom là đem lại hạnh phúc và an lạc cho đối tác và khách hàng. Sản phẩm của Intracom cũng với tinh thần như vậy, tất cả phải gửi tâm vào trong đó, đó là cả tình yêu, niềm tin của Intracom tạo ra từng sản phẩm, từng ngôi nhà và đưa đến cho khách hàng, cho mọi người. Khi anh phục vụ tốt, đem lại rất nhiều điều tốt đẹp cho cuộc đời, lẽ dĩ nhiên anh cũng có phần, dù là phần nhỏ thôi nhưng lại không kém vinh dự và đó chính là hạnh phúc rồi…”</p>\r\n<p style=\"text-align: center;\"> </p>\r\n<p style=\"text-align: center;\"><img src=\"public/uploads/demo/image-620.png\" alt=\"\" width=\"165\" height=\"40\" /> </p>\r\n<p style=\"text-align: center;\"><strong>Ông Nguyễn Thanh Việt</strong></p>\r\n<p style=\"text-align: center;\">Chủ tịch Intracom</p>\r\n<p style=\"text-align: center;\">Chủ tịch Hội đồng thành viên Bệnh viện Đa khoa Phương Đông</p>', NULL, 'Nội dung', NULL, 1, NULL, NULL, 61, 'EDITOR', 3, '2020-11-10 17:11:08', '2021-09-17 09:07:31', 0, NULL, NULL, 2);
