INSERT INTO `doctors` VALUES (1, 'Hoàng Văn Tuyết', 'hoang-van-tuyet', '{\"id\":12,\"name\":\"untitled-1.png\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T09:07:43.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"untitled-1.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"134.96 KB\\\",\\\"date\\\":1630055253,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/untitled-1.png\\\",\\\"width\\\":259,\\\"height\\\":400,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/untitled-1.png\\\"}\",\"updated_at\":\"2021-08-27T09:07:43.000000Z\",\"trash\":0}', '[{\"id\":15,\"name\":\"1583482378-700114575-khoa-nhi.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T20:47:41.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"1583482378-700114575-khoa-nhi.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"223.99 KB\\\",\\\"date\\\":1630097250,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/1583482378-700114575-khoa-nhi.jpg\\\",\\\"width\\\":2000,\\\"height\\\":1330,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/1583482378-700114575-khoa-nhi.jpg\\\"}\",\"updated_at\":\"2021-08-27T20:47:41.000000Z\",\"trash\":0},{\"id\":8,\"name\":\"layer-683.png\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T09:07:23.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"layer-683.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"341.75 KB\\\",\\\"date\\\":1630055233,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/layer-683.png\\\",\\\"width\\\":748,\\\"height\\\":419,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/layer-683.png\\\"}\",\"updated_at\":\"2021-08-27T09:07:23.000000Z\",\"trash\":0},{\"id\":14,\"name\":\"kham-phu-khoa-2-0.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T10:06:49.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"kham-phu-khoa-2-0.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"29.11 KB\\\",\\\"date\\\":1630058804,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/kham-phu-khoa-2-0.jpg\\\",\\\"width\\\":600,\\\"height\\\":399,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/kham-phu-khoa-2-0.jpg\\\"}\",\"updated_at\":\"2021-08-27T10:06:49.000000Z\",\"trash\":0}]', 'TTND.TS.Bác sĩ Cao cấp Hoàng Văn Tuyết với gần 40 năm kinh nghiệm đã tham gia vào công tác khám chữa bệnh, phòng bệnh ở các bệnh viện tuyến Trung ương như: Bệnh viện Bạch Mai; Bệnh viện Nhiệt Đới Trung ương.', '[{\"content\":\"<p>TTND.TS.B&aacute;c sĩ Cao cấp Ho&agrave;ng Văn Tuyết với gần 40 năm kinh nghiệm đ&atilde; tham gia v&agrave;o c&ocirc;ng t&aacute;c kh&aacute;m chữa bệnh, ph&ograve;ng bệnh ở c&aacute;c bệnh viện tuyến Trung ương như: Bệnh viện Bạch Mai; Bệnh viện Nhiệt Đới Trung ương.</p>\\n<p>&nbsp;</p>\",\"name\":\"Giới thiệu\",\"img\":\"public/uploads/doi-ngu-bac-si/icon-dn.png\"},{\"content\":\"<p>B&aacute;c sĩ Ho&agrave;ng Văn Tuyết l&agrave; người c&oacute; nhiều kinh nghiệm trong c&aacute;c c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch quốc gia, như: dịch c&uacute;m AH5N1 năm 2005, dịch ti&ecirc;u chảy cấp năm 2007, dịch H1N1 năm 2009, dịch Ebola, dịch MERS &ndash; CoV, dịch sởi &nbsp;&hellip;</p>\",\"name\":\"Kinh nghiệm\",\"img\":\"public/uploads/doi-ngu-bac-si/kinh-nghiem-dt.png\"},{\"content\":\"<p>Thầy thuốc nh&acirc;n d&acirc;n (2017), Hu&acirc;n chương Lao động hạng III, Bằng khen của Thủ tướng Ch&iacute;nh phủ, Bằng khen của Bộ Y tế, Lưu niệm chương V&igrave; sự nghiệp chăm s&oacute;c sức khỏe nh&acirc;n d&acirc;n, Kỷ niệm chương V&igrave; sự nghiệp khoa học kỹ thuật, Kỷ niệm chương V&igrave; thế hệ trẻ</p>\",\"name\":\"Thành tựu\",\"img\":\"public/uploads/doi-ngu-bac-si/qua-trinh.png\"}]', '[{\"content\":\"<p>TTND.TS.B&aacute;c sĩ Cao cấp Ho&agrave;ng Văn Tuyết với gần 40 năm kinh nghiệm đ&atilde; tham gia v&agrave;o c&ocirc;ng t&aacute;c kh&aacute;m chữa bệnh, ph&ograve;ng bệnh ở c&aacute;c bệnh viện tuyến Trung ương như: Bệnh viện Bạch Mai; Bệnh viện Nhiệt Đới Trung ương.<br /><br />B&aacute;c sĩ Ho&agrave;ng Văn Tuyết l&agrave; người c&oacute; nhiều kinh nghiệm trong c&aacute;c c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch quốc gia, như: dịch c&uacute;m AH5N1 năm 2005, dịch ti&ecirc;u chảy cấp năm 2007, dịch H1N1 năm 2009, dịch Ebola, dịch MERS &ndash; CoV, dịch sởi &nbsp;&hellip;</p>\",\"name\":\"Giới thiệu\"},{\"content\":\"<p>1983: Tốt nghiệp Đại học Y H&agrave; Nội<br />1994: Tốt nghiệp B&aacute;c sỹ Chuy&ecirc;n khoa I, Đại học Y H&agrave; Nội<br />2003: Tiến sỹ y học, Đại học Y H&agrave; Nội<br />2010: B&aacute;c sỹ cao cấp</p>\",\"name\":\"Quá trình đào tạo\"},{\"content\":\"<p>1980 - 1984: B&aacute;c sỹ khoa huyết học v&agrave; truyền m&aacute;u BV Bạch Mai.<br />1984 - 1987: B&aacute;c sỹ Cục qu&acirc;n y tiền phương.<br />1987 - 1989: B&aacute;c sỹ khoa Kh&aacute;m bệnh BV Bạch Mai.<br />1989 - 2004: Ph&oacute; trưởng khoa y học hạt nh&acirc;n v&agrave; điều trị ung bướu BV Bạch Mai.<br />2004 - 2018: Ph&oacute; Gi&aacute;m đốc Bệnh viện Nhiệt đới Trung ương<br />2018 - nay: Trưởng Khoa Kh&aacute;m Bệnh, Bệnh viện Đa khoa Phương Đ&ocirc;ng</p>\",\"name\":\"Quá trình công tác\"},{\"content\":\"<p><span data-offset-key=\\\"5egva-0-0\\\">Lĩnh vực: Nội chung, Ung thư, Truyền nhiễm</span></p>\\n<p>Với kinh nghiệm gần 40 năm trong nghề, TTND.TS.BSCC Ho&agrave;ng Văn Tuyết đ&atilde; tham gia v&agrave;o c&ocirc;ng t&aacute;c kh&aacute;m chữa bệnh, ph&ograve;ng bệnh ở c&aacute;c bệnh viện tuyến Trung ương như: Bệnh viện Bạch Mai; Bệnh viện Nhiệt Đới Trung ương.</p>\\n<p>B&aacute;c sĩ Ho&agrave;ng Văn Tuyết l&agrave; người c&oacute; nhiều kinh nghiệm trong c&aacute;c c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch quốc gia, như: dịch c&uacute;m AH5N1 năm 2005, dịch&nbsp;ti&ecirc;u chảy&nbsp;cấp năm 2007, dịch H1N1 năm 2009, dịch Ebola, dịch MERS &ndash; CoV, dịch sởi &nbsp;&hellip; Ngo&agrave;i ra, với tư c&aacute;ch l&agrave; th&agrave;nh vi&ecirc;n ban chỉ đạo x&acirc;y dựng v&agrave; &aacute;p dụng hệ thống quản l&yacute; chất lượng, BS Ho&agrave;ng Văn Tuyết đ&atilde; c&ugrave;ng Ban l&atilde;nh đạo, tập thể c&aacute;n bộ CNV Bệnh viện Bạch Mai đưa Bệnh viện trở th&agrave;nh đơn vị đầu ti&ecirc;n trong Bộ Y tế triển khai đồng bộ hệ thống quản l&yacute; chất lượng ISO tại 100% c&aacute;c khoa, ph&ograve;ng, trung t&acirc;m.</p>\\n<p>Đặc biệt, TTND.TS.BSCC Ho&agrave;ng Văn Tuyết l&agrave; một trong những người s&aacute;ng lập v&agrave; khai sinh ra Khoa điều trị ung bướu &ndash; một lĩnh vực chuy&ecirc;n khoa h&oacute;a s&acirc;u về điều trị ung thư trong lịch sử gần 100 năm th&agrave;nh lập Bệnh viện Bạch Mai.</p>\",\"name\":\"Kinh nghiệm khám chữa bệnh\"},{\"content\":\"<ol>\\n<li>Thầy thuốc nh&acirc;n d&acirc;n (2017)</li>\\n<li>Hu&acirc;n chương Lao động hạng III</li>\\n<li>Bằng khen của Thủ tướng Ch&iacute;nh phủ</li>\\n<li>Bằng khen của Bộ Y tế</li>\\n<li>Lưu niệm chương V&igrave; sự nghiệp chăm s&oacute;c sức khỏe nh&acirc;n d&acirc;n</li>\\n<li>Kỷ niệm chương V&igrave; sự nghiệp khoa học kỹ thuật</li>\\n<li>Kỷ niệm chương V&igrave; thế hệ trẻ</li>\\n</ol>\",\"name\":\"Các thành tích đã đạt được\"},{\"content\":\"<table class=\\\"wp-block-table\\\">\\n<tbody>\\n<tr>\\n<td><strong>TT</strong></td>\\n<td><strong>T&ecirc;n ph&aacute;t minh s&aacute;ng chế</strong><strong>đề t&agrave;i khoa học</strong></td>\\n<td><strong>Chủ tr&igrave; đề t&agrave;i</strong></td>\\n<td><strong>Cấp c&ocirc;ng nhận, năm</strong></td>\\n</tr>\\n<tr>\\n<td>1</td>\\n<td>Nồng độ Calxitoninne trong huyết thanh người b&igrave;nh thường( định lượng bằng đo ph&oacute;ng xạ IRMA)</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Tạp ch&iacute; Y học thực h&agrave;nh số 2 (394)/2001 Bộ Y tế</td>\\n</tr>\\n<tr>\\n<td>2</td>\\n<td>Nồng độ chất chỉ điểm khối u CA 15-3 của người b&igrave;nh thường ( định lượng bằng đo ph&oacute;ng xạ IRMA)</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Tạp ch&iacute; Y học thực h&agrave;nh số 2 (394)/2001 Bộ Y tế</td>\\n</tr>\\n<tr>\\n<td>3</td>\\n<td>Nồng độ chất chỉ điểm khối u CA 72-4 của người b&igrave;nh thường( định lượng bằng ph&oacute;ng xạ IRMA)</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Tạp ch&iacute; Y học thực h&agrave;nh số 1 (393)/2001 Bộ Y tế</td>\\n</tr>\\n<tr>\\n<td>4</td>\\n<td>Nồng độ Thyroglobin huyết thanh người b&igrave;nh thường được x&aacute;c định bằng phương ph&aacute;p IRMA</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Tạp ch&iacute; Y học thực h&agrave;nh số 4 (396)/2001 Bộ Y tế</td>\\n</tr>\\n<tr>\\n<td>5</td>\\n<td>Nghi&ecirc;n cứu ảnh hưởng của Phospho ph&oacute;ng xạ (P32) trong điều trị giảm đau ung thư di căn xương l&ecirc;n hệ tạo m&aacute;u v&agrave; một số chỉ ti&ecirc;u sinh h&oacute;a</td>\\n<td>Chủ tr&igrave;</td>\\n<td>Tạp ch&iacute; Y dược học qu&acirc;n sự. Học viện qu&acirc;n y số đặc san năm 2004</td>\\n</tr>\\n<tr>\\n<td>6</td>\\n<td>Điều trị giảm đau ung thư di căn xương bằng phospho ph&oacute;ng xạ 32P</td>\\n<td>Chủ tr&igrave;</td>\\n<td>BB nghiệm thu ng&agrave;y 10/4/2004</td>\\n</tr>\\n<tr>\\n<td>7</td>\\n<td>Đ&aacute;nh gi&aacute; t&aacute;c dụng to&agrave;n th&acirc;n v&agrave; tại chỗ trong điều trị giảm đau do ung thư di căn xương bằng phospho &ndash; 32</td>\\n<td>Chủ tr&igrave;</td>\\n<td>B&aacute;o c&aacute;o khoa học Hội nghị Ph&aacute;p &ndash; Việt h&igrave;nh ảnh y học v&agrave; y học hạt nh&acirc;n lần thứ 2 (2003)</td>\\n</tr>\\n<tr>\\n<td>8</td>\\n<td>Nhận x&eacute;t bước đầu điều trị ung thư tuyến gi&aacute;p thể biệt h&oacute;a bằng 131-I tại Khoa Y học hạt nh&acirc;n Bệnh viện Bạch Mai</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>B&aacute;o c&aacute;o khoa học Hội nghị Ph&aacute;p &ndash; Việt h&igrave;nh ảnh y học v&agrave; y học hạt nh&acirc;n lần thứ 2 (2003)</td>\\n</tr>\\n<tr>\\n<td>9</td>\\n<td>Bước đầu ứng dụng phương ph&aacute;p đo ph&oacute;ng xạ miễn dịch (IRMA) định lượng một số chất chỉ điểm khối u trong ung thư</td>\\n<td>Chủ tr&igrave;</td>\\n<td>BB nghiệm thu ng&agrave;y 21/3/2000</td>\\n</tr>\\n<tr>\\n<td>10</td>\\n<td>Nghi&ecirc;n cứu ứng dụng điều trị giảm đau ung thư di căn xương bằng phospho ph&oacute;ng xạ P-32</td>\\n<td>Chủ tr&igrave;</td>\\n<td>Tạp ch&iacute; Y học Thực h&agrave;nh số 489 năm 2004</td>\\n</tr>\\n<tr>\\n<td>11</td>\\n<td>Nghi&ecirc;n cứu đặc điểm l&acirc;m s&agrave;ng, x&acirc;y dựng quy tr&igrave;nh chẩn đo&aacute;n v&agrave; điều trị bệnh tả</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Quyết định số 207B/QĐ&amp;NĐQG ng&agrave;y 28/10/2008</td>\\n</tr>\\n<tr>\\n<td>12</td>\\n<td>So s&aacute;nh Oseltamivit liều cao với liều chuẩn để điều trị c&uacute;m nặng v&agrave; c&uacute;m gia cầm</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Quyết định số 144/QĐ &ndash; VT&amp;NĐQG ng&agrave;y 03/8/2009</td>\\n</tr>\\n<tr>\\n<td>13</td>\\n<td>Nghi&ecirc;n cứu sản xuất một số bộ kit đa năng để ph&aacute;t hiện một số vi sinh vật g&acirc;y bệnh truyền nhiễm v&agrave; đ&aacute;nh gi&aacute; thể trạng miễn dịch của người bệnh</td>\\n<td>Chủ nhiệm Đề t&agrave;i nh&aacute;nh</td>\\n<td>Giấy chứng nhận số 7790/GCN&ndash; TTKHCN ng&agrave;y 12/3/2010 của Bộ KHCN</td>\\n</tr>\\n<tr>\\n<td>14</td>\\n<td>Nghi&ecirc;n cứu đặc điểm dịch tễ học, l&acirc;m s&agrave;ng, phương ph&aacute;p chẩn đo&aacute;n, điều trị, dự ph&ograve;ng bệnh ch&acirc;n tay miệng tại Việt Nam</td>\\n<td>Th&agrave;nh vi&ecirc;n Ban điều h&agrave;nh</td>\\n<td>Quyết định nghiệm thu số 2220/QĐ&ndash; BKHCN ng&agrave;y 26/8/2014</td>\\n</tr>\\n<tr>\\n<td>15</td>\\n<td>Nghi&ecirc;n cứu chẩn đo&aacute;n sớm v&agrave; điều trị bệnh tả c&oacute; biến chứng trụy mạch, suy thận, nhiễm toan chuyển h&oacute;a v&agrave; rối loạn điện giải</td>\\n<td>Th&agrave;nh vi&ecirc;n</td>\\n<td>Quyết định số 4228/QĐ &ndash; BYT ng&agrave;y 8/11/2011</td>\\n</tr>\\n<tr>\\n<td>16</td>\\n<td>Nghi&ecirc;n cứu ứng dụng kỹ thuật sinh học ph&acirc;n tử x&aacute;c định mức độ kh&aacute;ng kh&aacute;ng sinh của một số chủng vi khuẩn g&acirc;y bệnh thường gặp ở Việt nam</td>\\n<td>Th&agrave;nh vi&ecirc;n ban điều h&agrave;nh</td>\\n<td>Th&aacute;ng 02/2016</td>\\n</tr>\\n</tbody>\\n</table>\",\"name\":\"Các đề tài, công trình nghiên cứu khoa học\"}]', 'Trưởng Khoa Khám Bệnh', 'TTND.TS.Bác sĩ', 2, NULL, 1, 1, '2021-08-30 02:22:04', '2021-09-14 09:42:52', NULL, NULL, NULL);
INSERT INTO `doctors` VALUES (2, 'Trần Kinh Trang', 'tran-kinh-trang', '{\"id\":103,\"name\":\"tran-kim-trang.png\",\"title\":\"Trần Kinh Trang\",\"caption\":\"Trần Kinh Trang\",\"alt\":\"Trần Kinh Trang\",\"description\":\"Trần Kinh Trang\",\"created_at\":\"2021-09-07T04:50:20.000000Z\",\"parent\":41,\"is_file\":1,\"path\":\"public/uploads/doi-ngu-bac-si/\",\"file_name\":\"tran-kim-trang.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"165.34 KB\\\",\\\"date\\\":1630990220,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/tran-kim-trang.png\\\",\\\"width\\\":344,\\\"height\\\":494,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/tran-kim-trang.png\\\"}\",\"updated_at\":\"2021-09-07T04:50:20.000000Z\",\"trash\":0}', '[{\"id\":15,\"name\":\"1583482378-700114575-khoa-nhi.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T20:47:41.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"1583482378-700114575-khoa-nhi.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"223.99 KB\\\",\\\"date\\\":1630097250,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/1583482378-700114575-khoa-nhi.jpg\\\",\\\"width\\\":2000,\\\"height\\\":1330,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/1583482378-700114575-khoa-nhi.jpg\\\"}\",\"updated_at\":\"2021-08-27T20:47:41.000000Z\",\"trash\":0},{\"id\":8,\"name\":\"layer-683.png\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T09:07:23.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"layer-683.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"341.75 KB\\\",\\\"date\\\":1630055233,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/layer-683.png\\\",\\\"width\\\":748,\\\"height\\\":419,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/layer-683.png\\\"}\",\"updated_at\":\"2021-08-27T09:07:23.000000Z\",\"trash\":0},{\"id\":14,\"name\":\"kham-phu-khoa-2-0.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T10:06:49.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"kham-phu-khoa-2-0.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"29.11 KB\\\",\\\"date\\\":1630058804,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/kham-phu-khoa-2-0.jpg\\\",\\\"width\\\":600,\\\"height\\\":399,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/kham-phu-khoa-2-0.jpg\\\"}\",\"updated_at\":\"2021-08-27T10:06:49.000000Z\",\"trash\":0}]', 'Bác sĩ CKII Trần Kinh Trang là chuyên gia Nhi khoa có kinh nghiệm trên 30 năm, Nguyên Trưởng khoa tim mạch Bệnh viện Nhi Trung Ương. Bác sĩ đã trực tiếp khám và điều trị cho hàng chục nghìn bệnh nhi trên toàn quốc, cũng như tham gia với vai trò chuyên gia trong các chương trình tư vấn về sức khỏe nhi khoa uy tín.', '[{\"content\":\"<p>B&aacute;c sĩ CKII Trần Kinh Trang l&agrave; chuy&ecirc;n gia Nhi khoa c&oacute; kinh nghiệm tr&ecirc;n 30 năm, Nguy&ecirc;n Trưởng khoa tim mạch Bệnh viện Nhi Trung Ương. B&aacute;c sĩ đ&atilde; trực tiếp kh&aacute;m v&agrave; điều trị cho h&agrave;ng chục ngh&igrave;n bệnh nhi tr&ecirc;n to&agrave;n quốc, cũng như tham gia với vai tr&ograve; chuy&ecirc;n gia trong c&aacute;c chương tr&igrave;nh tư vấn về sức khỏe nhi khoa uy t&iacute;n.</p>\",\"name\":\"Giới thiệu\",\"img\":\"public/uploads/doi-ngu-bac-si/icon-dn.png\"},{\"content\":\"<p>B&aacute;c sĩ CKII Trần Kinh Trang hiện đang l&agrave; Trưởng Khoa Nhi, BVĐK Phương Đ&ocirc;ng. B&aacute;c sĩ trực tiếp l&agrave;m việc tại ph&ograve;ng kh&aacute;m Nhi, BVĐK Phương Đ&ocirc;ng. Mọi vấn đề về sức khỏe trẻ em như tim bẩm sinh, v&agrave;ng da, c&uacute;m m&ugrave;a, ti&ecirc;u chảy,...sẽ được B&aacute;c sĩ tận t&igrave;nh thăm kh&aacute;m v&agrave; đưa ra chẩn đo&aacute;n ch&iacute;nh x&aacute;c để c&oacute; một ph&aacute;c đồ điều trị hiệu quả nhất cho b&eacute;.</p>\",\"name\":\"Kinh nghiệm\",\"img\":\"public/uploads/doi-ngu-bac-si/kinh-nghiem-dt.png\"}]', '[{\"content\":\"<p>Bác sĩ CKII Trần Kinh Trang là chuyên gia Nhi khoa có kinh nghiệm trên 30 năm, Nguyên Trưởng khoa tim mạch Bệnh viện Nhi Trung Ương. Bác sĩ đã trực tiếp khám và điều trị cho hàng chục nghìn bệnh nhi trên toàn quốc, cũng như tham gia với vai trò chuyên gia trong các chương trình tư vấn về sức khỏe nhi khoa uy tín.</p>\",\"name\":\"Giới thiệu\"},{\"content\":\"<p>Bác sĩ CKII Trần Kinh Trang hiện đang là Trưởng Khoa Nhi, BVĐK Phương Đông. Bác sĩ trực tiếp làm việc tại phòng khám Nhi, BVĐK Phương Đông. Mọi vấn đề về sức khỏe trẻ em như tim bẩm sinh, vàng da, cúm mùa, tiêu chảy,...sẽ được Bác sĩ tận tình thăm khám và đưa ra chẩn đoán chính xác để có một phác đồ điều trị hiệu quả nhất cho bé.</p>\",\"name\":\"Kinh nghiệm\"}]', 'Trưởng Khoa Nhi', 'Bác sĩ CKII Nhi khoa - Siêu âm tim', 1, NULL, 1, 1, '2021-08-30 02:22:04', '2021-09-14 10:05:55', NULL, NULL, NULL);
INSERT INTO `doctors` VALUES (3, 'Hàn Văn Bạ', 'han-van-ba', '{\"id\":105,\"name\":\"bs-han-van-ba-1.png\",\"title\":\"Hàn Văn Bạ\",\"caption\":\"Hàn Văn Bạ\",\"alt\":\"Hàn Văn Bạ\",\"description\":\"Hàn Văn Bạ\",\"created_at\":\"2021-09-07T04:56:28.000000Z\",\"parent\":41,\"is_file\":1,\"path\":\"public/uploads/doi-ngu-bac-si/\",\"file_name\":\"bs-han-van-ba-1.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"149.96 KB\\\",\\\"date\\\":1630990588,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/bs-han-van-ba-1.png\\\",\\\"width\\\":514,\\\"height\\\":467,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/doi-ngu-bac-si\\\\/bs-han-van-ba-1.png\\\"}\",\"updated_at\":\"2021-09-07T04:56:28.000000Z\",\"trash\":0}', '[{\"id\":15,\"name\":\"1583482378-700114575-khoa-nhi.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T20:47:41.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"1583482378-700114575-khoa-nhi.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"223.99 KB\\\",\\\"date\\\":1630097250,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/1583482378-700114575-khoa-nhi.jpg\\\",\\\"width\\\":2000,\\\"height\\\":1330,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/1583482378-700114575-khoa-nhi.jpg\\\"}\",\"updated_at\":\"2021-08-27T20:47:41.000000Z\",\"trash\":0},{\"id\":8,\"name\":\"layer-683.png\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T09:07:23.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"layer-683.png\",\"extra\":\"{\\\"extension\\\":\\\"png\\\",\\\"size\\\":\\\"341.75 KB\\\",\\\"date\\\":1630055233,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/layer-683.png\\\",\\\"width\\\":748,\\\"height\\\":419,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/layer-683.png\\\"}\",\"updated_at\":\"2021-08-27T09:07:23.000000Z\",\"trash\":0},{\"id\":14,\"name\":\"kham-phu-khoa-2-0.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T10:06:49.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"kham-phu-khoa-2-0.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"29.11 KB\\\",\\\"date\\\":1630058804,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/kham-phu-khoa-2-0.jpg\\\",\\\"width\\\":600,\\\"height\\\":399,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/kham-phu-khoa-2-0.jpg\\\"}\",\"updated_at\":\"2021-08-27T10:06:49.000000Z\",\"trash\":0}]', 'TTUT, Bác sĩ Chuyên khoa II Hàn Văn Bạ có trên 35 năm kinh nghiệm trong lĩnh vực Đa khoa Ngoại Sản, đã điều trị cho hàng nghìn bệnh nhân gặp các vấn đề như: sỏi tiết niệu, u xơ, u nang, viêm lộ tuyến cổ tử cung, u xơ tử cung,...', '[{\"content\":\"<p>TTUT, B&aacute;c sĩ Chuy&ecirc;n khoa II H&agrave;n Văn Bạ c&oacute; tr&ecirc;n 35 năm kinh nghiệm trong lĩnh vực Đa khoa Ngoại Sản, đ&atilde; điều trị cho h&agrave;ng ngh&igrave;n bệnh nh&acirc;n gặp c&aacute;c vấn đề như: sỏi tiết niệu, u xơ, u nang, vi&ecirc;m lộ tuyến cổ tử cung, u xơ tử cung,...</p>\",\"name\":\"Giới thiệu\",\"img\":\"public/uploads/doi-ngu-bac-si/icon-dn.png\"},{\"content\":\"<p>Thầy thuốc ưu t&uacute; B&aacute;c sĩ Chuy&ecirc;n khoa II H&agrave;n Văn Bạ sở hữu tr&ecirc;n 35 năm kinh nghiệm trong lĩnh vực Đa khoa Ngoại Sản.</p>\\n<p>- Kh&aacute;m, điều trị sỏi tiết niệu. Kh&aacute;m, điều trị u xơ, u nang, s&oacute;n tiểu, c&aacute;c rối loạn tiền m&atilde;n kinh, m&atilde;n kinh.&nbsp;</p>\\n<p>- Khám và đi&ecirc;̀u trị các b&ecirc;̣nh lý phụ khoa. Soi cổ tử cung, lấy bệnh phẩm x&eacute;t nghiệm.</p>\\n<p>- C&aacute;c bệnh l&yacute; vi&ecirc;m &acirc;m đạo do nấm, tr&ugrave;ng roi, tạp khuẩn, do thay đổi nội tiết tố.</p>\\n<p>- C&aacute;c bệnh l&yacute; cổ tử cung: vi&ecirc;m lộ tuyến cổ tử cung, vi&ecirc;m cổ tử cung, polyp tử cung, u xơ tử cung, nh&acirc;n xơ tử cung, lạc nội mạc tử cung&hellip;&nbsp;</p>\",\"name\":\"Kinh nghiệm\",\"img\":\"public/uploads/doi-ngu-bac-si/kinh-nghiem-dt.png\"},{\"content\":\"<p>Tốt nghiệp B&aacute;c sĩ Chuy&ecirc;n khoa II chuy&ecirc;n ng&agrave;nh đa khoa Ngoại sản. Từ 1984 - 2001: B&aacute;c sĩ Khoa Ngoại -&nbsp;Bệnh viện E. Từ 2001 - 2015: Trưởng Khoa Ngoại tổng hợp - Bệnh viện E</p>\",\"name\":\"Quá trình học tập và công tác\",\"img\":\"public/uploads/doi-ngu-bac-si/qua-trinh.png\"}]', '[{\"content\":\"<p>TTUT, Bác sĩ Chuyên khoa II Hàn Văn Bạ có trên 35 năm kinh nghiệm trong lĩnh vực Đa khoa Ngoại Sản, đã điều trị cho hàng nghìn bệnh nhân gặp các vấn đề như: sỏi tiết niệu, u xơ, u nang, viêm lộ tuyến cổ tử cung, u xơ tử cung,...</p>\",\"name\":\"Giới thiệu\"},{\"content\":\"<p>+ Tốt nghiệp Bác sĩ Chuyên khoa II chuyên ngành đa khoa Ngoại sản</p>\\n<p>+ 1984 - 2001: Bác sĩ Khoa Ngoại - Bệnh viện E</p>\\n<p>+ 2001 - 2015: Trưởng Khoa Ngoại tổng hợp - Bệnh viện E</p>\",\"name\":\"Quá trình học tập và công tác\"},{\"content\":\"<p>Thầy thuốc ưu tú Bác sĩ Chuyên khoa II Hàn Văn Bạ sở hữu trên 35 năm kinh nghiệm trong lĩnh vực Đa khoa Ngoại Sản</p>\\n<p>+ Khám, điều trị sỏi tiết niệu<br />+ Khám, điều trị u xơ, u nang, són tiểu, các rối loạn tiền mãn kinh, mãn kinh<br />+ Khám và điều trị các bệnh lý phụ khoa<br />+ Soi cổ tử cung, lấy bệnh phẩm xét nghiệm<br />+ Các bệnh lý viêm âm đạo do nấm, trùng roi, tạp khuẩn, do thay đổi nội tiết tố…<br />+ Các bệnh lý cổ tử cung: viêm lộ tuyến cổ tử cung, viêm cổ tử cung, polyp tử cung, u xơ tử cung, nhân xơ tử cung, lạc nội mạc tử cung…<br />+ Các bệnh lý buồng trứng như u nang buồng trứng, viêm buồng trứng, tắc vòi trứng, ứ nước vòi trứng, dính vòi trứng…<br />+ Các phẫu thuật nội soi trong phụ khoa: điều trị u nang buồng trứng, u xơ tử cung</p>\",\"name\":\"Kinh nghiệm khám chữa bệnh\"}]', 'Trưởng Khoa ngoại', 'TTUT. Bác sĩ CKII Ngoại khoa', 6, NULL, 1, 1, '2021-08-30 02:22:04', '2021-09-14 10:10:02', NULL, NULL, NULL);
