INSERT INTO `news_categories` VALUES (51, 'Xét nghiệm', 'xet-nghiem', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:16', '2021-09-25 04:12:16', 'Thông tin về Xét nghiệm | Bệnh viện Đa khoa Phương Đông', '', 'Hiểu đúng về xét nghiệm cần phải thực hiện kiểm tra sức khỏe tìm dấu hiệu hoặc tác nhân gây bệnh. Từ đó sớm phát hiện bệnh trên cơ thể và điều trị hiệu quả', 'https://benhvienphuongdong.vn/admin/category/edit-categories/51?type=news', 51);
INSERT INTO `news_categories` VALUES (52, 'Bệnh truyền nhiễm', 'benh-truyen-nhiem', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:16', '2021-09-25 04:12:16', 'Kiến thức Bệnh truyền nhiễm | Bệnh viện Đa khoa Phương Đông', '', 'Bệnh truyền nhiễm hay còn được gọi là bệnh lây là do vi sinh vật (vi khuẩn, vi rút, ký sinh trùng...) gây ra. Có khả năng lây lan từ người này sang người khác một cách trực tiếp hoặc gián tiếp', 'https://benhvienphuongdong.vn/admin/category/edit-categories/52?type=news', 52);
INSERT INTO `news_categories` VALUES (53, 'Bệnh ung thư', 'benh-ung-thu', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:17', '2021-09-25 04:12:17', 'Bệnh ung thư là gì? Nguyên nhân, triệu chứng và cách điều trị', 'bệnh ung thư,chẩn đoán ung thư,điều trị ung thư', 'Ung thư là một nhóm các bệnh liên quan đến việc tăng sinh tế bào một cách mất kiểm soát và những tế bào đó có khả năng xâm lấn những mô khác bằng cách phát triển trực tiếp vào mô lân cận hoặc di chuyển đến những cơ phần khác trong cơ thể', 'https://benhvienphuongdong.vn/admin/category/edit-categories/53?type=news', 53);
INSERT INTO `news_categories` VALUES (54, 'Tai mũi họng', 'tai-mui-hong', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:17', '2021-09-25 04:12:17', 'Bệnh tai mũi họng: Nguyên nhân, triệu chứng, chấn đoán và điều trị', 'bệnh tai mũi họng', 'Các bệnh lý tai mũi họng thường gặp: Nghe kém, Polyp mũi, Thủng màng nhĩ, Ù tai, Ung thư cổ họng, Ung thư vòm họng, Viêm đau họng, Viêm mũi dị ứng, Viêm tai giữa, Viêm thanh quản, Khàn tiếng, Viêm xoang cấp', 'https://benhvienphuongdong.vn/admin/category/edit-categories/54?type=news', 54);
INSERT INTO `news_categories` VALUES (55, 'Răng hàm mặt', 'rang-ham-mat', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:18', '2021-09-25 04:12:18', 'Thông tin về Răng hàm mặt | Bệnh viện Đa khoa Phương Đông', 'kiến thức nha khoa,răng hàm mặt', 'Tư vấn, chẩn đoán các bệnh lý liên quan tới răng hàm mặt bởi đội ngũ chuyên gia Bệnh viện Phương Đông. Từ đó giúp chăm sóc sức khỏe răng miệng một cách tốt hơn', 'https://benhvienphuongdong.vn/admin/category/edit-categories/55?type=news', 55);
INSERT INTO `news_categories` VALUES (56, 'Nam khoa', 'nam-khoa', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:19', '2021-09-25 04:12:19', 'Thông tin các bệnh lý Nam khoa - Tư vấn cùng các bác sĩ chuyên khoa', 'nam khoa,bệnh lý nam giới', 'Điểm danh các bệnh lý nam khoa thường gặp ở nam giới. Tìm hiểu thông tin chi tiết về các bệnh cùng phương pháp điều trị hiệu quả nhất', 'https://benhvienphuongdong.vn/admin/category/edit-categories/56?type=news', 56);
INSERT INTO `news_categories` VALUES (57, 'Tiêu hoá', 'tieu-hoa', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:19', '2021-09-25 04:12:19', 'Thông tin về các bệnh lý tiêu hoá thường gặp và cách điều trị', 'bệnh đường tiêu hóa', 'Cung cấp các thông tin dấu hiệu nhận biết các bệnh tiêu hóa và cách chữa bệnh tiêu hóa tốt nhất. Từ đó giúp người bệnh có cách chăm sóc sức khỏe tốt nhất cho mình', 'https://benhvienphuongdong.vn/admin/category/edit-categories/57?type=news', 57);
INSERT INTO `news_categories` VALUES (58, 'Tim mạch', 'tim-mach', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:20', '2021-09-25 04:12:20', 'Thông tin Tim mạch | Bệnh viện Đa khoa Phương Đông', 'bệnh tim mạch,suy tim,hở van tim,tăng huyết áp', 'Cung cấp thông tin mới về bệnh tim mạch, cách điều trị, chế độ ăn uống, cho người bệnh suy tim, mắc các bệnh tim mạch như tăng huyết áp, huyết áp thấp, hẹp hở van tim', 'https://benhvienphuongdong.vn/admin/category/edit-categories/58?type=news', 58);
INSERT INTO `news_categories` VALUES (59, 'Thần kinh', 'than-kinh', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:21', '2021-09-25 04:12:21', 'Thông tin bệnh thần kinh | Bệnh viện Đa khoa Phương Đông', '', 'Cung cấp thông tin các bệnh thần kinh thường gặp, căn nguyên, sinh lý bệnh, triệu chứng, chẩn đoán và cách điều trị bệnh hiệu quả nhất cho người bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/59?type=news', 59);
INSERT INTO `news_categories` VALUES (60, 'Xương khớp', 'xuong-khop', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:21', '2021-09-25 04:12:21', 'Bệnh cơ xương khớp: Nguyên nhân và cách điều trị', 'xương khớp,co xương khơp,thấp khớp,thoái hóa khớp', 'Cung cấp các thông tin về bệnh xương khớp thường gặp giúp nhận biết, hiểu rõ nguyên nhân, triệu chứng và cách điều trị bệnh cơ xương khớp tốt nhất.', 'https://benhvienphuongdong.vn/admin/category/edit-categories/60?type=news', 60);
INSERT INTO `news_categories` VALUES (61, 'Da liễu', 'da-lieu', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:22', '2021-09-25 04:12:22', 'Thông tin các bệnh Da liễu: Nguyên nhân, triệu chứng và cách điều trị', '', 'Cung cấp các thông tin về bệnh lý da liễu hay gặp, các bệnh lý về rối loạn da, tóc, móng và các bệnh lây truyền qua đường tình dục. Từ đó giúp điều trị sớm bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/61?type=news', 61);
INSERT INTO `news_categories` VALUES (62, 'Mắt', 'mat', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:22', '2021-09-25 04:12:22', 'Thông tin về Mắt, tật khúc xạ: Nguyên nhân, triệu chứng, cách điều trị', 'bệnh lý về mắt,tật về mắt', 'Cung cấp thông tin các bệnh về mắt, tật khúc xạ, cận thị, loạn thị, nhược thị,...Tìm hiểu rõ nguyên nhân, dấu hiệu nhận biết từ đó có phương pháp điều trị sớm phù hợp hơn', 'https://benhvienphuongdong.vn/admin/category/edit-categories/62?type=news', 62);
INSERT INTO `news_categories` VALUES (63, 'Dinh dưỡng', 'dinh-duong', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:23', '2021-09-25 04:12:23', 'Dinh dưỡng - Cẩm nang về chế độ dinh dưỡng tốt cho sức khỏe', '', 'Chia sẻ kiến thức về dinh dưỡng, tư vấn chế độ ăn uống dinh dưỡng đảm bảo sức khỏe cho gia đình đặc biệt là trẻ em, phụ nữ mang thai và người cao tuổi', 'https://benhvienphuongdong.vn/admin/category/edit-categories/63?type=news', 63);
INSERT INTO `news_categories` VALUES (64, 'Bệnh trẻ em', 'benh-tre-em', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:24', '2021-09-25 04:12:24', 'Thông tin Bệnh trẻ em | Bệnh viện Đa khoa Phương Đông', 'bệnh trẻ em,bệnh trẻ em thường gặp,bệnh trẻ em gặp khi giao mùa', 'Nguyên nhân, dấu hiệu nhận biết các bệnh thường gặp ở trẻ em đặc biệt là khi giao mùa để cha mẹ có thể phòng tránh và chữa trị kịp thời cho bé nhà mình', 'https://benhvienphuongdong.vn/admin/category/edit-categories/64?type=news', 64);
INSERT INTO `news_categories` VALUES (65, 'Hô hấp', 'ho-hap', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:24', '2021-09-25 04:12:24', 'Bệnh Hô hấp: Nguyên nhân, triệu chứng, chẩn đoán và điều trị', 'bệnh hô hấp,các bệnh hô hấp thường gặp', 'Cung cấp các kiến thức có liên quan tới các bệnh lý về đường hô hấp: nguyên nhân, triệu chứng, cách nhận biết và phương pháp điều trị phù hợp tốt cho sức khỏe người bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/65?type=news', 65);
INSERT INTO `news_categories` VALUES (67, 'Tiết niệu', 'tiet-nieu', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:25', '2021-09-25 04:12:25', 'Thông tin Tiết niệu | Bệnh viện Đa khoa Phương Đông', '', 'Cung cấp thông tin về bệnh đường tiết niệu, nguyên nhân, triệu chứng, cách nhận biết và phương pháp điều trị hiệu quả tốt nhất cho người bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/67?type=news', 67);
INSERT INTO `news_categories` VALUES (68, 'Tiêm chủng', 'tiem-chung', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:26', '2021-09-25 04:12:26', 'Thông tin Tiêm chủng | Bệnh viện Đa khoa Phương Đông', 'tiêm chủng,lịch tiêm chủng,tiêm chủng cho bé', 'Chia sẻ các kiến thức liên quan tới tiêm chủng cho bé, phụ nữ mang thai,...Các triệu chứng thường gặp sau tiêm. Cách chăm sóc cho  bé sau khi đi tiêm chủng về', 'https://benhvienphuongdong.vn/admin/category/edit-categories/68?type=news', 68);
INSERT INTO `news_categories` VALUES (69, 'Sản khoa', 'san-khoa', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:26', '2021-09-25 04:12:26', 'Thông tin Sản khoa', '', 'Cung cấp kiến thức xoay quanh chủ đề khám thai, giải đáp thắc mắc khi mang thai, phòng tránh thai, thai nghén,..', 'https://benhvienphuongdong.vn/admin/category/edit-categories/69?type=news', 69);
INSERT INTO `news_categories` VALUES (70, 'Khám sức khỏe', 'kham-suc-khoe', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:27', '2021-09-25 04:12:27', 'Thông tin Khám sức khỏe | Bệnh viện Đa khoa Phương Đông', 'khám sức khỏe,gói khám sức khỏe,khám sức khỏe tổng quát', 'Khám sức khỏe là tổng hợp những danh mục bao gồm: khám bệnh, xét nghiệm, chẩn đoán hình ảnh, thăm dò chức năng…giúp khách hàng/người bệnh hiểu rõ tình trạng sức khoẻ của mình, phát hiện sớm bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/70?type=news', 70);
INSERT INTO `news_categories` VALUES (71, 'Chương trình ưu đãi', 'chuong-trinh-uu-dai', NULL, NULL, 0, '', '', 10, 1, '2021-09-25 04:12:34', '2021-09-25 04:12:34', 'Chương trình Ưu Đại Đặc Biệt tại Bệnh viện Đa khoa Phương Đông', '', 'Trang thông tin về các chương trình ưu đãi, quà tặng, khuyến mãi mới nhất tại Bệnh viện Đa khoa Phương Đông dành cho khách hàng tới khám chữa bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/71?type=news', 71);
INSERT INTO `news_categories` VALUES (72, 'Thông tin sức khỏe', 'thong-tin-suc-khoe', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:27', '2021-09-25 04:12:27', 'Thông tin sức khỏe | Bệnh viện Đa khoa Phương Đông', '', 'Chia sẻ những kiến thức về các bệnh lý ảnh hưởng tới sức khỏe. Dấu hiệu nhận biết, nguyên nhân và phương pháp điều trị tốt nhất cho người bệnh', 'https://benhvienphuongdong.vn/admin/category/edit-categories/72?type=news', 72);
INSERT INTO `news_categories` VALUES (73, 'Nhi', 'nhi', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:28', '2021-09-25 04:12:28', 'Các bệnh lý liên quan tới Nhi thường gặp và Cách điều trị', '', 'Cung cấp các thông tin bệnh lý liên quan tới Nhi. Giúp cha mẹ có cách chăm sóc cho trẻ nhà mình tốt hơn', 'https://benhvienphuongdong.vn/admin/category/edit-categories/73?type=news', 73);
INSERT INTO `news_categories` VALUES (81, 'Bệnh gan mật', 'benh-gan-mat', NULL, NULL, 0, '', '', 1, 1, '2021-09-25 04:12:29', '2021-09-25 04:12:29', 'Thông tin Bệnh gan mật | Bệnh viện Đa khoa Phương Đông', '', 'Cung cấp thông tin chẩn đoán và điều trị các bệnh về gan mật bao gồm: nguyên nhân, triệu chứng, chẩn đoán, điều trị bệnh,...Các xét nghiệm cần thiết,...', 'https://benhvienphuongdong.vn/admin/category/edit-categories/81?type=news', 81);
INSERT INTO `news_categories` VALUES (82, 'Phụ khoa', 'phu-khoa', NULL, NULL, 0, '', '', 0, 1, '2021-09-25 04:12:14', '2021-09-25 04:12:14', 'Thông tin về các bệnh phụ khoa hay gặp ở nữ giới', '', 'Cung cấp thông tin các bệnh liên quan tới các cơ quan sinh dục nữ, bao gồm các bệnh viêm nhiễm cơ quan sinh dục dưới (âm hộ, âm đạo, cổ tử cung) và cơ quan sinh dục trên (tử cung, tai vòi và buồng trứng)...', 'https://benhvienphuongdong.vn/admin/category/edit-categories/82?type=news', 82);
