INSERT INTO `video_gallery` VALUES (1, 'Hơp tác toàn diện với viện công nghệ', 'hop-tac-toan-dien-voi-vien-cong-nghe', '{\"id\":15,\"name\":\"1583482378-700114575-khoa-nhi.jpg\",\"title\":\"\",\"caption\":\"\",\"alt\":\"\",\"description\":\"\",\"created_at\":\"2021-08-27T20:47:41.000000Z\",\"parent\":1,\"is_file\":1,\"path\":\"public/uploads/demo/\",\"file_name\":\"1583482378-700114575-khoa-nhi.jpg\",\"extra\":\"{\\\"extension\\\":\\\"jpg\\\",\\\"size\\\":\\\"223.99 KB\\\",\\\"date\\\":1630097250,\\\"isfile\\\":1,\\\"dir\\\":\\\"public\\\\/uploads\\\\/demo\\\\/\\\",\\\"path\\\":\\\"public\\\\/uploads\\\\/demo\\\\/1583482378-700114575-khoa-nhi.jpg\\\",\\\"width\\\":2000,\\\"height\\\":1330,\\\"thumb\\\":\\\"public\\\\/uploads\\\\/demo\\\\/thumbs\\\\/def\\\\/1583482378-700114575-khoa-nhi.jpg\\\"}\",\"updated_at\":\"2021-08-27T20:47:41.000000Z\",\"trash\":0}', 'https://www.youtube.com/watch?v=XDrMAYDW2Xk', '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/XDrMAYDW2Xk\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Tọa lạc trên diện tích gần 10ha, sở hữu lợi thế không gian rộng lớn và cơ sở vật chất hiện đại, Bệnh viện Đa khoa Phương Đông có mục tiêu trở thành bệnh viện ngoài công lập lớn nhất thủ đô cùng chiến lược thu hút khách hàng khu vực trong và ngoài nước. Trên tinh thần \"Chữa bệnh là chữa cả Tâm bệnh lẫn Thân bệnh\", Phương Đông tự tin là địa chỉ uy tín để chăm sóc sức khỏe cho mọi người, từng bước sánh vai với các bệnh viện quốc tế trong khu vực và trên thế giới.', '<p>Không gian thiết kế theo mô hình Bệnh viện - Khách sạn 5 sao Bệnh viện Đa khoa Phương Đông sở hữu quy mô lên tới 1000 giường bệnh, là đơn vị y tế tiên phong tại Việt Nam ây dựng theo mô hình bệnh viện xanh kết hợp điều trị và nghỉ dưỡng thuần tự nhiên. Tòa nhà chính được bao quanh bởi khuôn viên xanh mát với hơn 300 loại cây, hồ điều hòa, quảng trường rộng lớn với tổng diện tích gần 10ha. Khách hàng được tiếp cận mô hình dịch vụ hoàn hảo, BHYT thông tuyến 3 không cần chuyển viện, bảo lãnh các loại thẻ bảo hiểm phi nhân thọ, dịch vụ chăm sóc khách hàng tiện lợi, không phải chờ đợi, được đáp ứng mọi nhu cầu chăm sóc sức khỏe như đang trải nghiệm tại một khách sạn cao cấp.</p>', 'Lưu hà', NULL, 1, 1, 1, '2021-09-07 10:56:24', '2021-09-07 11:28:56', NULL, NULL, NULL);
